    public List<Integer> spiralOrder1(int[][] matrix) {
        List<Integer> order = new ArrayList<>();
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return order;
        }
        int rows = matrix.length, columns = matrix[0].length;
        boolean[][] visited = new boolean[rows][columns];
        int total = rows * columns;
        int row = 0, column = 0;
        int[][] directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        int directionIndex = 0;
        for (int i = 0; i < total; i++) {
            order.add(matrix[row][column]);
            visited[row][column] = true;
            int nextRow = row + directions[directionIndex][0], nextColumn = column + directions[directionIndex][1];
            if (nextRow < 0 || nextRow >= rows || nextColumn < 0 || nextColumn >= columns || visited[nextRow][nextColumn]) {
                directionIndex = (directionIndex + 1) % 4;
            }
            row += directions[directionIndex][0];
            column += directions[directionIndex][1];
        }
        return order;
    }
    public List<Integer> spiralOrder(int[][] matrix) {
        List<Integer> order = new ArrayList<>();
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return order;
        }
        int left = 0, right = matrix[0].length - 1, top = 0, bottom = matrix.length - 1;
        while (left <= right && top <= bottom) {
            for (int column = left; column <= right; column++) {
                order.add(matrix[top][column]);
            }
            for (int row = top + 1; row <= bottom; row++) {
                order.add(matrix[row][right]);
            }
            if (left < right && top < bottom) {
                for (int column = right - 1; column > left; column--) {
                    order.add(matrix[bottom][column]);
                }
                for (int row = bottom; row > top; row--) {
                    order.add(matrix[row][left]);
                }
            }
            left++;
            right--;
            top++;
            bottom--;
        }
        return order;
    }
    public boolean lemonadeChange(int[] bills) {
        int five = 0, ten = 0;
        for (int bill : bills) {
            if(bill == 5){
                five++;
            }else if(bill == 10){
                if (five < 1){
                    return false;
                }
                five--;
                ten++;
            }else {
                if (five >= 1 && ten >= 1){
                    five--;
                    ten--;
                }else if(five >= 3){
                    five -= 3;
                }else {
                    return false;
                }
            }
        }
        return true;
    }
    public int largestPerimeter(int[] nums) {
        int n = nums.length;
        int Perimeter = 0;
        Arrays.sort(nums);
        for (int i = n - 1; i >= 0; i--) {
            if (nums[i] < nums[i - 1] + nums[i - 2]){
                return nums[i] + nums[i - 1] + nums[i - 2];
            }
        }
        return Perimeter;
    }
    public boolean checkStraightLine(int[][] coordinates) {
        int k ,b ;//y = kx + b; b = y - kx
        if (coordinates[1][0] - coordinates[0][0] == 0){
            for (int[] coordinate : coordinates) {
                if (coordinates[0][1] != coordinate[1]) {
                    return false;
                }
                if (coordinates[0][0] != coordinate[0]) {
                    return false;
                }
            }
        }else {
            k = coordinates[1][1] - coordinates[0][1] / coordinates[1][0] - coordinates[0][0];
            b = coordinates[1][1] - k * coordinates[1][0];
            for (int[] coordinate : coordinates) {
                if (coordinate[1] != (k * coordinate[0] + b)) {
                    return false;
                }
            }
        }
        return true;
    }
